sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
        var Input = request.body.data;
        //var pathparams = request.pathParams;
        //var incident_type = pathparams.incident_type;
        //var stgRec = new GlideRecord('x_941504_armorblox_abx_incidents');
        if (Input.incident_type == "THREAT_INCIDENT_TYPE") {
			var threats_rec = new GlideRecord('x_941504_armorblox_threat_incidents');
            threats_rec.initialize();
			threats_rec.incident_id = Input.id;
            threats_rec.incident_type = Input.incident_type;
            threats_rec.insert();
            //stgRec.query();
            //while (stgRec.next()) {
            //   stgRec.deleteRecord();
        } else if (Input.incident_type == "ABUSE_INCIDENT_TYPE") {
			var abuse_rec = new GlideRecord('x_941504_armorblox_abuse_incidents');
            abuse_rec.initialize();
            abuse_rec.incident_id = Input.id;
            abuse_rec.incident_type = Input.incident_type;
            abuse_rec.insert();
        } else  
	{
			var dlp_rec = new GlideRecord('x_941504_armorblox_dlp_incidents');
            dlp_rec.initialize();
            dlp_rec.incident_id = Input.id;
            dlp_rec.incident_type = Input.incident_type;
            dlp_rec.insert();
        }
    }
)(request, response);
