function Getincidentsdata() {
    try {
        var r = new sn_ws.RESTMessageV2('global.Get_incidents_data', 'Armorblox_incidents');
        var ArmorbloxAPI = gs.getProperty('global.Armorblox_API');
        r.setStringParameterNoEscape('Armorblox_API', 'g9nCCM6sNZ2CBt12UWgGA2IX+i3e6TP0MuwZ1Oqh6KU=');
		r.setStringParameterNoEscape('incident_id', '73346');
        //gs.info("Armorblox APIKey ="+ArmorbloxAPI);
        var response = r.execute();
        var responseBody = response.getBody();//gs.info("Response Body =" + responseBody);
        var httpStatus = response.getStatusCode();
        gs.info("Parsedemo" + responseBody);



    } catch (ex) {
        var message = ex.message;
    }
}
